mapper.select:
SELECT u.nickname, b.boardCode, b.boardTitle, b.boardContent, DATE_FORMAT(b.boardWritedate, '%Y-%m-%d') AS boardWritedate, b.boardViews FROM boardtbl b JOIN usertbl u ON b.userCode = u.userCode
AND b.boardType = #{boardType}
AND b.boardType >= 1 AND b.boardType <= 5
ORDER BY b.boardWritedate DESC LIMIT #{pageSize} OFFSET #{offset}

mapper.update:
   	UPDATE boardtbl
	SET boardViews= boardViews+1
	WHERE boardCode=#{boardCode}

update boardtbl 
SET 
	boardImgOrigin =#{boardImgOrigin},boardImgReal =#{boardImgReal},boardTitle=#{boardTitle}, boardContent=#{boardContent},
	boardTheme=#{boardTheme},boardTourdays=#{boardTourdays}, boardType=#{boardType}
WHERE 
	boardCode = #{boardCode}
