mapper.select:
SELECT userId 
from usertbl
where userName = #{userName}
AND userTel = #{userTel}

SELECT userPw 
from usertbl 
where userId = #{userId} 
AND userName = #{userName}
AND userTel = #{userTel}

mapper.update:
update usertbl 
set userPw = #{userPw} 
where userId = #{userId}

update usertbl
set userPoint = #{pointAmount}
where userCode = #{userCode}
